INSERT INTO `#__acymailing_mail`  VALUES ('1', 'New Subscriber on your website : {user:email}', '<p>Hello {subtag:name},</p><p>A new user has been created in AcyMailing : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_created', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('2', 'A User unsubscribed from all your lists : {user:email}', '<p>Hello {subtag:name},</p><p>The user {user:name} : {user:email} unsubscribed from all your lists</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_unsuball', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('3', 'A User unsubscribed : {user:email}', '<p>Hello {subtag:name},</p><p>The user {user:name} : {user:email} unsubscribed from your list</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_unsub', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('4', 'A User refuses to receive e-mails from your website : {user:email}', '<p>The User {user:name} : {user:email} refuses to receive any e-mail anymore from your website.</p><p>Subscription : {user:subscription}</p><p>{survey}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_refuse', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('5', 'New contact from your website : {user:email}', '<p>Hello {subtag:name},</p><p>A user submitted the form : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_contact', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('6', 'A user subscribed or modified his subscription : {user:email}', '<p>Hello {subtag:name},</p><p>A user submitted the form : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_contact_menu', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('7', 'A user confirmed his subscription : {user:email}', '<p>Hello {subtag:name},</p><p>A user confirmed his subscription : </p><blockquote><p>Name : {user:name}</p><p>Email : {user:email}</p><p>IP : {user:ip} </p><p>Subscription : {user:subscription}</p></blockquote>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'notification_confirm', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('8', '{subtag:name|ucfirst}, {trans:PLEASE_CONFIRM_SUB}', '<div style=\"text-align: center; width: 100%; background-color: #ffffff;\">\r\n			<table style=\"text-align:justify; margin:auto; background-color:#ebebeb; border:1px solid #e7e7e7\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"600\" align=\"center\" bgcolor=\"#ebebeb\">\r\n			<tbody>\r\n			<tr style=\"line-height: 0px;\">\r\n			<td style=\"line-height: 0px;\" height=\"38px\"><img src=\"media/com_acymailing/templates/newsletter-4/top.png\" border=\"0\" alt=\" - - - \" /></td>\r\n			</tr>\r\n			<tr>\r\n			<td style=\"text-align:center\" width=\"600\">\r\n			<table style=\"margin:auto;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"520\">\r\n			<tbody>\r\n			<tr>\r\n			<td style=\"background-color: #ffffff; border: 1px solid #dbdbdb; padding: 20px; width: 500px; margin: 15px auto; text-align: left;\">\r\n			<h1>Hello {subtag:name|ucfirst},</h1>\r\n			<p>{trans:CONFIRM_MSG}<br /><br />{trans:CONFIRM_MSG_ACTIVATE}</p>\r\n			<br />\r\n			<p style=\"text-align:center;\"><strong>{confirm}{trans:CONFIRM_SUBSCRIPTION}{/confirm}</strong></p>\r\n			</td>\r\n			</tr>\r\n			</tbody>\r\n			</table>\r\n			</td>\r\n			</tr>\r\n			<tr style=\"line-height: 0px;\">\r\n			<td style=\"line-height: 0px;\" height=\"40px\"><img src=\"media/com_acymailing/templates/newsletter-4/bottom.png\" border=\"0\" alt=\" - - - \" /></td>\r\n			</tr>\r\n			</tbody>\r\n			</table>\r\n			</div>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'confirmation', NULL, '1', '1', NULL, NULL, 'a:3:{s:6:\"action\";s:7:\"confirm\";s:13:\"actionbtntext\";s:28:\"{trans:CONFIRM_SUBSCRIPTION}\";s:9:\"actionurl\";s:19:\"{confirm}{/confirm}\";}', NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('9', 'AcyMailing Cron Report {mainreport}', '<p>{report}</p><p>{detailreport}</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'report', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, ''), ('10', 'Modify your subscription', '<p>Hello {subtag:name}, </p><p>You requested some changes on your subscription,</p><p>Please {modify}click here{/modify} to be identified as the owner of this account and then modify your subscription.</p>', '', '1', NULL, NULL, NULL, NULL, NULL, NULL, 'notification', '0', NULL, 'modif', NULL, '1', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, NULL, '');
